'use strict';

var temp = require('ds18b20');
var Promise = require('bluebird');

module.exports = {
  sensors: function() {
    return new Promise(function(resolve, reject) {
      temp.sensors(function(err, ids) {
	if (err) reject(err);
	else resolve(ids);
      });
    });
  },
  temperature: function(sensorId) {
    return new Promise(function(resolve, reject) {
      temp.temperature(sensorId, function(err, value) {
	if (err) reject(err);
	else resolve(value);
      });
    });
  }
};
